    public static String formatTemperature(String temp){
        if(null == temp)
            return "";
        float value = 0;
        try {
            value = Float.parseFloat(temp);
        }
        catch(Exception e){

        }
        return formatTemperature(value);
    }
    public static String formatTemperature(float temperature){
        return String.valueOf(temperature + "Â°");
    }
    public static Date getDateFromString(String time){
        SimpleDateFormat format = new SimpleDateFormat(SERVERDATETIMEFORMAT, Locale.getDefault());
        try {
            Date date = format.parse(time);
            return date;
        } catch (ParseException e) {
        }
        return null;
    }
    public static String getDisplayDate(Date date, String outputformat){
        SimpleDateFormat dDateFormat = new SimpleDateFormat(outputformat, Locale.ENGLISH);
        try{
            return dDateFormat.format(date);
        }
        catch(Exception e){
            return "";
        }
    }
    public static String getDisplayDate(Date date){
        return getDisplayDate(date, DISPLAYDATEFORMAT);
    }
    public static String getDisplayDateYear(Date date){
        return getDisplayDate(date, DISPLAYDATEFORMATYEAR);
    }
    public static String getDisplayTime(Date datetime){
        SimpleDateFormat dTimeFormat = new SimpleDateFormat(Helper.DISPLAYTIMEFORMAT,Locale.ENGLISH);
        try{
            return dTimeFormat.format(datetime);
        }
        catch(Exception e){
            return "";
        }
    }
    public static String getDayOfWeek(Date date){
        Calendar calendar = Calendar.getInstance();
        calendar.setTime(date);
        String dayOfWeek = calendar.getDisplayName(Calendar.DAY_OF_WEEK, Calendar.SHORT, Locale.getDefault());
        return dayOfWeek;
    }
    public static boolean isTodaysDate(Date date){
        Calendar calendar = Calendar.getInstance();
        String todaysDate = getDisplayDateYear(calendar.getTime());
        String checkDate = getDisplayDateYear(date);
        if(todaysDate.equalsIgnoreCase(checkDate))
            return true;
        else
            return false;
    }
